import Slider from 'react-slick'
import styles from './styles.module.css'
import {useRef} from 'react'

function get_item(obj, key) {
	console.log(obj)
	return <> <h1 key={key}>{obj["full-name"]}</h1> </>
}

function Team(props) {

	const sliderRef = useRef(null)
	console.log(sliderRef)

	function SampleNextArrow(props) {
		const {styles, img_path} = props
		return <>
		 		<button className={styles.nextBtn + " slick-arrow slick-next"} onClick = {() => sliderRef.current.slickNext()}>
			 		<div className={styles.imgBlock}>
		 				<img src={img_path} alt=""/>
		 			</div>
				</button>
			</>
	}

	function SamplePrevArrow(props) {
		const {styles, img_path} = props
		return <>
				<button className={styles.prevBtn}>
					<div className={styles.imgBlock} onClick = {() => sliderRef.current.slickPrev()}>
		 				<img src={img_path} alt=""/>
		 			</div>
				</button>
			</>
	}


	const  slideCards = [
		{
			'full-name': "Алеша попович"
		},
		{	
			'full-name': "Никита Васин"
		},
		{
			'full-name': "Алексей Васькин"
		}
	]

	const sliderConfigs = {
		slidesToShow: 1,
    	slidesToScroll: 1,
    	infinite: false,
    	arrows: true,
    	nextArrow: SampleNextArrow({"styles":styles, "img_path":"../../../next-arrow.svg"}),
    	prevArrow: SamplePrevArrow({"styles":styles, "img_path":"../../../prev-arrow.svg"})
	}

	console.log(styles)

	return <div className={styles.slider}>
			<Slider {...sliderConfigs}>
				{slideCards.map(get_item)}
			</Slider>
		</div>
}

export default Team
